import React, { useEffect, useRef } from 'react';

const Header = () => {
  const hamMenuBtnRef = useRef(null);
  const smallMenuRef = useRef(null);
  const headerHamMenuBtnRef = useRef(null);
  const headerHamMenuCloseBtnRef = useRef(null);
  const headerSmallMenuLinksRef = useRef([]);
  const headerLogoContainerRef = useRef(null);

  useEffect(() => {
    const hamMenuBtn = hamMenuBtnRef.current;
    const smallMenu = smallMenuRef.current;
    const headerHamMenuBtn = headerHamMenuBtnRef.current;
    const headerHamMenuCloseBtn = headerHamMenuCloseBtnRef.current;
    const headerSmallMenuLinks = headerSmallMenuLinksRef.current;

    const handleHamMenuClick = () => {
      if (smallMenu.classList.contains('header__sm-menu--active')) {
        smallMenu.classList.remove('header__sm-menu--active');
      } else {
        smallMenu.classList.add('header__sm-menu--active');
      }

      if (headerHamMenuBtn.classList.contains('d-none')) {
        headerHamMenuBtn.classList.remove('d-none');
        headerHamMenuCloseBtn.classList.add('d-none');
      } else {
        headerHamMenuBtn.classList.add('d-none');
        headerHamMenuCloseBtn.classList.remove('d-none');
      }
    };

    const handleSmallMenuLinkClick = () => {
      smallMenu.classList.remove('header__sm-menu--active');
      headerHamMenuBtn.classList.remove('d-none');
      headerHamMenuCloseBtn.classList.add('d-none');
    };

    const handleLogoClick = () => {
      window.location.href = '/';
    };

    hamMenuBtn.addEventListener('click', handleHamMenuClick);

    headerSmallMenuLinks.forEach((link) => {
      link.addEventListener('click', handleSmallMenuLinkClick);
    });

    headerLogoContainerRef.current.addEventListener('click', handleLogoClick);

    return () => {
      hamMenuBtn.removeEventListener('click', handleHamMenuClick);
      headerSmallMenuLinks.forEach((link) => {
        link.removeEventListener('click', handleSmallMenuLinkClick);
      });
      headerLogoContainerRef.current.removeEventListener('click', handleLogoClick);
    };
  }, []);

  return (
    <header>
      <div className="header__logo-container" ref={headerLogoContainerRef}>
        {/* Logo content */}
      </div>
      <div className="header__main-ham-menu-cont" ref={hamMenuBtnRef}>
        <button className="header__main-ham-menu">Menu</button>
        <button className="header__main-ham-menu-close d-none">Close</button>
      </div>
      <nav className="header__sm-menu" ref={smallMenuRef}>
        <ul>
          <li>
            <a href="/" className="header__sm-menu-link" ref={(el) => (headerSmallMenuLinksRef.current[0] = el)}>
              Home
            </a>
          </li>
          <li>
            <a href="/about" className="header__sm-menu-link" ref={(el) => (headerSmallMenuLinksRef.current[1] = el)}>
              About
            </a>
          </li>
          {/* More links here */}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
